// eslint-disable-next-line no-unused-vars
let playerType = 'playerType'
let player = 'x';

let gameReducer = (array, action) => {
    switch (action.type) {
        case playerType:
            console.log(this.array)
            const element = this.array.find((item) => item.id === action.id);
            element.name = player;
            return this.array;
        default:
            return this.array;
    }
}
export default gameReducer

// eslint-disable-next-line no-unused-vars
